import React, {Component} from 'react'
import './App.css'  
import Web3 from 'web3'

class App extends Component {

    async Web3() {
        if(window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window
        }
    }

    constructor(props) {
        super(props)
        this.state = {
            account: '0x0'
        }

    }

    render() {
        return(
            <div>
                <h>my header</h>
            </div>
        )
    }
}

export default App;
